import DoctorModel from "../models/doctorModel.js";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import AppointmentModel from "../models/appointmentModel.js";
import { response } from "express";

const doctorList = async (request, response) => {
  try {
    //excluding with email, password means not show in the json parse data.
    const doctors = await DoctorModel.find({}).select(["-email", "-password"]);
    response.json({ success: true, doctors });
  } catch (error) {
    console.log(error);
    response.json({ success: false, message: error.message });
  }
};
//API for doctor Login
const signInDoctor = async (request, response) => {
  try {
    const { email, password } = request.body;
    const doctor = await DoctorModel.findOne({ email });
    if (!doctor) {
      return response.json({ success: false, message: "Invalid credentials" });
    }
    const isMatch = await bcrypt.compare(password, doctor.password);
    // console.log("isMatch_signInDoctor_43", isMatch); //true
    if (!isMatch) {
      return response.json({ success: false, message: "Invalid credentials" });
    }

    if (isMatch) {
      const doctorToken = jwt.sign({ id: doctor._id }, process.env.JWT_SECRET);
      return response.json({
        success: true,
        doctorToken,
        doctor,
        message: "Doctor Signed In Successfully",
      });
    } else {
      return response.json({ success: false, message: "Invalid credentials" });
    }
  } catch (error) {
    console.log(error);
    return response.json({ success: false, message: "Doctor Signin Failed" });
  }
};
