const appointmentComplete = async (request, response) => {
  try {
    const { docId, appointmentId } = request.body;
    console.log("appointmentComplete body:", request.body);

    // check appointment belongs to doctor
    const appointmentData = await AppointmentModel.findOne({
      _id: appointmentId,
      docId: docId,
    });

    if (appointmentData) {
      await AppointmentModel.findByIdAndUpdate(appointmentId, {
        isCompleted: true,
      });
      return response.json({ success: true, message: "Appointment Completed" });
    } else {
      return response.json({ success: false, message: "Mark Failed" });
    }
  } catch (error) {
    console.log(error);
    response.json({ success: false, message: error.message });
  }
};

// API to cancel appointment
const appointmentCancel = async (request, response) => {
  try {
    const { docId, appointmentId } = request.body;
    console.log("appointmentCancel body:", request.body);

    const appointmentData = await AppointmentModel.findOne({
      _id: appointmentId,
      docId: docId,
    });

    if (appointmentData) {
      await AppointmentModel.findByIdAndUpdate(appointmentId, {
        cancelled: true,
      });
      return response.json({ success: true, message: "Appointment Cancelled" });
    } else {
      return response.json({ success: false, message: "Cancellation Failed" });
    }
  } catch (error) {
    console.log(error);
    response.json({ success: false, message: error.message });
  }
};
//API to get dashboard data for doctor panel
const doctorDashboard = async (request, response) => {
  try {
    const docId = request.doctorId; // comes from authDoctor middleware
    const appointments = await AppointmentModel.find({ docId });
    let earnings = 0;
    appointments.map((item) => {
      if (item.isCompleted || item.payment) {
        earnings += item.amount;
      }
    });
    let patients = [];
    appointments.map((item) => {
      if (patients.includes(item.userId)) {
        patients.push(item.userId);
      }
    });
    const doctorDashboardData = {
      earnings,
      appointments: appointments.length,
      patients: patients.length,
      latestAppointments: appointments.reverse().slice(0, 5),
    };
    response.json({ success: true, doctorDashboardData });
  } catch (error) {
    console.log(error);
    response.json({ success: false, message: error.message });
  }
};
//API to get doctor profile for Doctor Panel
const doctorProfile = async (request, response) => {
  try {
    const docId = request.doctorId; // comes from authDoctor middleware
    // console.log("docId_doctorProfile_doctorController", docId);//6884e3426944cd531c6f2e8c
    const profileData = await DoctorModel.findById(docId).select("-password");
    response.json({ success: true, profileData });
  } catch (error) {
    console.log(error);
    response.json({ success: false, message: error.message });
  }
};
//API to update doctor profile data from Doctor Panel
const updateDoctorProfile = async (request, response) => {
  try {
    const docId = request.doctorId; // comes from authDoctor middleware
    const { fees, address, available } = request.body;
    const updatedDoctorDetails = await DoctorModel.findByIdAndUpdate(docId, {
      fees,
      address,
      available,
    });

    return response.json({
      success: true,
      message: "Profile Updated",
      updatedDoctorDetails,
    });
  } catch (error) {
    console.log(error);
    return response.json({
      success: false,
      message: "Profile Updation Failed",
    });
  }
};
